mapper.select:
select count(email)
from user
where email = #{email}

select count(code)
from emailauth
where email = #{email}

select count(email)
from emailauth
where email = #{email} and code = #{code}

select salt
from user
where email = #{email}

select userno, username, email
from user
where email = #{email}

select userno, username, email, salt, password, profile
from user
where userno = #{userno}

select username, email, profile, introduce
from user
where userno = #{userno}

select userno, username, email, introduce
from user
where email = #{email} and password = #{password}
